import { useState } from 'react';
import { getFlashcardsByLesson } from '../data/courses/network-plus/flashcards/index.js';
import '../styles/FlashcardActivity.css';

function FlashcardActivity({ lessonId }) {
  const [cards, setCards] = useState(() => {
    const lessonCards = getFlashcardsByLesson(lessonId);
    const shuffled = [...lessonCards];
    for (let i = shuffled.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
    }
    return shuffled;
  });

  const [currentIndex, setCurrentIndex] = useState(0);
  const [flipped, setFlipped] = useState(false);
  const [mastered, setMastered] = useState(new Set());

  const handleFlip = () => {
    setFlipped(!flipped);
  };

  const handleNext = () => {
    if (currentIndex < cards.length - 1) {
      setCurrentIndex(currentIndex + 1);
      setFlipped(false);
    }
  };

  const handlePrevious = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
      setFlipped(false);
    }
  };

  const handleMaster = () => {
    setMastered(prev => new Set([...prev, cards[currentIndex].id]));
    if (currentIndex < cards.length - 1) {
      handleNext();
    }
  };

  const handlePutBack = () => {
    if (currentIndex >= cards.length - 1) return; // Can't put back the last card

    const cardToMove = cards[currentIndex];
    const remainingCards = cards.filter((_, index) => index !== currentIndex);

    // Insert the card at a random position between the next card and the end
    const min = currentIndex; // Position in the new, smaller array
    const max = remainingCards.length;
    const randomInsertionIndex = Math.floor(Math.random() * (max - min + 1)) + min;

    remainingCards.splice(randomInsertionIndex, 0, cardToMove);

    setCards(remainingCards);
    setFlipped(false);
  };

  const handleRestart = () => {
    setMastered(new Set());
    setCurrentIndex(0);
    setFlipped(false);
  };

  if (cards.length === 0) {
    return (
      <div className="flashcard-activity">
        <div className="flashcard-header">
          <h2>üÉè Flashcards</h2>
          <p>No flashcards available for this lesson yet.</p>
        </div>
      </div>
    );
  }

  const currentCard = cards[currentIndex];
  const allMastered = mastered.size === cards.length;

  return (
    <div className="flashcard-activity">
      <div className="flashcard-header">
        <h2>üÉè Flashcards</h2>
        <div className="progress">
          Card {currentIndex + 1} of {cards.length} ‚Ä¢ Mastered: {mastered.size}/{cards.length}
        </div>
      </div>

      <div className={`flashcard ${flipped ? 'flipped' : ''}`} onClick={handleFlip}>
        <div className="flashcard-inner">
          <div className="flashcard-front">
            <div className="card-label">Question</div>
            <div className="card-content">{currentCard.front}</div>
            <div className="card-hint">Click to flip</div>
          </div>
          <div className="flashcard-back">
            <div className="card-label">Answer</div>
            <div className="card-content">{currentCard.back}</div>
            <div className="card-hint">Click to flip back</div>
          </div>
        </div>
      </div>

      <div className="flashcard-controls">
        <button 
          className="btn-nav" 
          onClick={handlePrevious}
          disabled={currentIndex === 0}
        >
          ‚Üê Previous
        </button>

        <button
          className="btn-put-back"
          onClick={handlePutBack}
          disabled={currentIndex === cards.length - 1 || mastered.has(currentCard.id)}
        >
          ‚Üª Put Back in Stack
        </button>

        <button 
          className="btn-master" 
          onClick={handleMaster}
          disabled={mastered.has(currentCard.id)}
        >
          {mastered.has(currentCard.id) ? '‚úì Mastered' : 'Mark as Mastered'}
        </button>
        
        <button 
          className="btn-nav" 
          onClick={handleNext}
          disabled={currentIndex === cards.length - 1}
        >
          Next ‚Üí
        </button>
      </div>

      {allMastered && (
        <div className="complete-message">
          <p>üéâ Congratulations! You've mastered all {cards.length} flashcards!</p>
          <button className="btn-restart" onClick={handleRestart}>
            Practice Again
          </button>
        </div>
      )}
    </div>
  );
}

export default FlashcardActivity;
