import { Link } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { networkPlusLessons } from '../data/courses/network-plus/lessons';
import '../styles/theme.css';
import '../styles/Dashboard.css';

function Dashboard() {
  const completedCount = networkPlusLessons.filter(l => l.completed).length;
  const rawProgress = (completedCount / networkPlusLessons.length) * 100;
  const daysRemaining = Math.max(0, 30 - completedCount);

  // simple mount animation for % readout and bar
  const [animated, setAnimated] = useState(0);
  useEffect(() => {
    const target = Math.round(rawProgress);
    let frame = 0;
    const id = requestAnimationFrame(function tick() {
      frame += 1;
      const eased = Math.min(target, Math.round((frame / 30) * target)); // ~0.5s
      setAnimated(eased);
      if (eased < target) requestAnimationFrame(tick);
    });
    return () => cancelAnimationFrame(id);
  }, [rawProgress]);

  return (
    <div className="dashboard" role="main" aria-label="CertifyStack Network Plus dashboard">
      {/* Header */}
      <header className="header">
        <div className="logo" aria-label="CertifyStack">
          <span className="logo-icon" aria-hidden>‚ö°</span>
          <span>CertifyStack</span>
        </div>
        <nav aria-label="Primary">
          <button className="nav-btn" type="button">üìö <span>Courses</span></button>
          <button className="nav-btn" type="button">üìä <span>Progress</span></button>
        </nav>
      </header>

      {/* Hero */}
      <section className="hero">
        <div className="hero-content">
          <h1>Network+ Certification</h1>
          <p className="hero-subtitle">
            Master networking fundamentals and pass N10-009 in 30 days.
          </p>
          <div className="hero-stats" aria-label="Dashboard stats">
            <div className="stat-item">
              <span className="stat-value">{networkPlusLessons.length}</span>
              <span className="stat-label">Total Lessons</span>
            </div>
            <div className="stat-item">
              <span className="stat-value">{completedCount}</span>
              <span className="stat-label">Completed</span>
            </div>
            <div className="stat-item">
              <span className="stat-value">{daysRemaining}</span>
              <span className="stat-label">Days to Go</span>
            </div>
          </div>
        </div>
      </section>

      {/* Progress */}
      <section className="progress-section" aria-label="Your progress">
        <div className="progress-card">
          <div className="progress-header">
            <h2>Your Progress</h2>
            <span className="progress-percentage" aria-live="polite">{animated}%</span>
          </div>
          <div className="progress-bar-container" role="progressbar"
               aria-valuemin={0} aria-valuemax={100} aria-valuenow={animated}>
            <div className="progress-bar-fill" style={{ width: `${animated}%` }} />
          </div>
          <div className="progress-details">
            <span>{completedCount} of {networkPlusLessons.length} lessons completed</span>
            <span>{networkPlusLessons.length - completedCount} remaining</span>
          </div>
        </div>
      </section>

      {/* Lessons */}
      <section className="lessons-section">
        <div className="section-header">
          <h2 className="section-title">Course Curriculum</h2>
          <p className="section-subtitle">
            30 comprehensive lessons covering all Network+ N10-009 exam objectives
          </p>
        </div>

        <div className="lessons-grid">
          {networkPlusLessons.map(lesson => {
            const locked = Boolean(lesson.locked);
            return (
              <div
                key={lesson.id}
                className={`lesson-card ${locked ? 'locked' : ''}`}
                aria-disabled={locked}
              >
                <div className="lesson-header-card">
                  <span className="lesson-number">Lesson {lesson.id}</span>
                  <span className="lesson-time">‚è± {lesson.time}</span>
                </div>

                <h3>{lesson.title}</h3>
                <p className="lesson-domain">üìÇ {lesson.domain}</p>

                {locked ? (
                  <button className="lesson-btn btn-locked" disabled aria-label="Lesson locked">
                    üîí Locked
                  </button>
                ) : (
                  <Link to={`/lesson/${lesson.id}`} aria-label={`Start lesson ${lesson.id}`}>
                    <button className="lesson-btn btn-start">‚ñ∂ Start Lesson</button>
                  </Link>
                )}
              </div>
            );
          })}
        </div>
      </section>
    </div>
  );
}

export default Dashboard;
